import React, { useState } from "react";

const useForm = () => {
    //Form values
    const [values, setValues] = useState({
        name: "",
    });
    //Errors
    const [errors, setErrors] = useState({});

    //A method to handle form inputs
    const handleChange = (event) => {
        const { name, value } = event.target;
        setValues({
            ...values,
            [name]: value,
        });
    };

    return {
        values,
        errors,
        handleChange,
    };
};

export default useForm;
