import { createStore, combineReducers } from "redux";

let defaultState = {
    name: "Shop",
    description: "Vitamins & Supplements",
};

const headerName = (state = defaultState, action) => {
    if(action.type === "Header")
    {
        return action.payload;
    }
    else if (action.type === "Cart") {
        return {
            name: "Cart",
            description: "Hooray! We Have your supplements ready.",
        };
    } else if (action.type === "Shop") {
        return defaultState;
    }
    return state;
};

const store = createStore(combineReducers({ headerName }));

export default store;
