const ProductDetail = (props) => {
    const { id } = useParams();
    const products = props.products;
    const index = products.findIndex((x) => x.id == id);
    const product = props.products[index];

    const radio = product.modifiers[1];
    const input = product.modifiers[0];

    const addToCartHandler = () => {
        // and then send Http request
        // fetch('firebase-url', { method: 'POST', body: JSON.stringify(newCart) })
        // dispatch(
        //     cartActions.addItemToCart({
        //         id,
        //         title,
        //         price,
        //     })
        // );
    };

    return (
        // <div className=" bg-slate-400 mt-5">
        //     <div>{product.name}</div>
        //     <div>{product.price}</div>
        //     <div>{parse(product.description)}</div>

        //     <img
        //         src={product.primary_image.url_standard}
        //         alt={product.primary_image.description}
        //         width="100"
        //         height="200"
        //     />
        //     <input
        //         type={input.type}
        //         id={input.id}
        //         name={input.name}
        //         placeholder={input.display_name}
        //         required={input.required}
        //         maxLength={input.text_max_length}
        //     />
        //     <input
        //         type={input.type}
        //         id={input.id}
        //         name={input.name}
        //         placeholder={input.display_name}
        //         required={input.required}
        //         maxLength={input.text_max_length}
        //     />
        //     <input
        //         type="radio"
        //         id="onetime"
        //         name={radio.name}
        //         value="onetime"
        //     />
        //     <label for="onetime">{radio.option_values[0].label}</label>
        //     <input
        //         type="radio"
        //         id="monthly"
        //         name={radio.name}
        //         value="monthly"
        //     />
        //     <label for="monthly">{radio.option_values[1].label}</label>
        //     <button onClick={addToCartHandler}>Add to Cart</button>
        //     <br></br>
        // </div>
        <div className="bg-white">
            <div className="pt-6 pb-16 sm:pb-24">
                <nav
                    aria-label="Breadcrumb"
                    className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8"
                >
                    <ol role="list" className="flex items-center space-x-4">
                        {product.breadcrumbs.map((breadcrumb) => (
                            <li key={breadcrumb.id}>
                                <div className="flex items-center">
                                    <a
                                        href={breadcrumb.href}
                                        className="mr-4 text-sm font-medium text-gray-900"
                                    >
                                        {breadcrumb.name}
                                    </a>
                                    <svg
                                        viewBox="0 0 6 20"
                                        xmlns="http://www.w3.org/2000/svg"
                                        aria-hidden="true"
                                        className="h-5 w-auto text-gray-300"
                                    >
                                        <path
                                            d="M4.878 4.34H3.551L.27 16.532h1.327l3.281-12.19z"
                                            fill="currentColor"
                                        />
                                    </svg>
                                </div>
                            </li>
                        ))}
                        <li className="text-sm">
                            <a
                                href={product.href}
                                aria-current="page"
                                className="font-medium text-gray-500 hover:text-gray-600"
                            >
                                {product.name}
                            </a>
                        </li>
                    </ol>
                </nav>
                <div className="mt-8 max-w-2xl mx-auto px-4 sm:px-6 lg:max-w-7xl lg:px-8">
                    <div className="lg:grid lg:grid-cols-12 lg:auto-rows-min lg:gap-x-8">
                        <div className="lg:col-start-8 lg:col-span-5">
                            <div className="flex justify-between">
                                <h1 className="text-xl font-medium text-gray-900">
                                    {product.name}
                                </h1>
                                <p className="text-xl font-medium text-gray-900">
                                    {product.price}
                                </p>
                            </div>
                            {/* Reviews */}
                            <div className="mt-4">
                                <h2 className="sr-only">Reviews</h2>
                                <div className="flex items-center">
                                    <p className="text-sm text-gray-700">
                                        {product.rating}
                                        <span className="sr-only">
                                            {" "}
                                            out of 5 stars
                                        </span>
                                    </p>
                                    <div className="ml-1 flex items-center">

                                    </div>
                                    <div
                                        aria-hidden="true"
                                        className="ml-4 text-sm text-gray-300"
                                    >
                                        ·
                                    </div>
                                    <div className="ml-4 flex">
                                        <a
                                            href="#"
                                            className="text-sm font-medium text-indigo-600 hover:text-indigo-500"
                                        >
                                            See all {product.reviewCount}{" "}
                                            reviews
                                        </a>
                                    </div>
                                </div>
                            </div>
                        </div>

                        {/* Image gallery */}
                        <div className="mt-8 lg:mt-0 lg:col-start-1 lg:col-span-7 lg:row-start-1 lg:row-span-3">
