const UPDATE = "headerUpdate/UPDATE";

export const changeColor = (color) => ({ type: UPDATE });

const initialState = {
    menu: "Shop",
    name: "Shop",
    description: "Vitamins & Supplements",
    price: null,
};

const updateHeader = (state = initialState, action) => {
    switch (action.type) {
        case UPDATE:
            return action.payload;
        default:
            return state;
    }
};

export default updateHeader;
