import { createStore, combineReducers } from "redux";

let defaultState = {
    menu: "Shop",
    name: "Shop",
    description: "Vitamins & Supplements",
    price: null,
};

const headerName = (state = defaultState, action) => {
    if (action.type === "Header") {
        return action.payload;
    }
    return state;
};

const store = createStore(combineReducers({ headerName }));

export default store;
