import React from "react";
import { useSelector } from "react-redux";
import "./Header.module.css";

const Header = () => {
    const state = useSelector((state) => state);
    const { name, title, description } = state.headerName;
    return (
        <header>
            <h2>{name}</h2>
            <h1 className="mt-1 text-4xl font-extrabold text-gray-900 sm:text-5xl sm:tracking-tight lg:text-6xl">{title}</h1>
            <p>{description}</p>
        </header>
    );
};

export default Header;
