import { useState } from "react";

const useForm = (validate, initPrice) => {
    const [values, setValues] = useState({
        customername: "",
        allergies: "",
        purchace: initPrice,
    });
    const [errors, setErrors] = useState({});

    const handleChange = (event) => {
        const { name, value, id } = event.target;
        console.log(name,value,id);

        if (id === "subscribe") {
            console.log(1);
            value.purchace = String(initPrice - initPrice * 0.1);
        } else if (id === "ontime") {
            value.purchace = String(initPrice);
            console.log(2);

        }

        setValues({
            ...values,
            [name]: value,
        });

        console.log(id);
    };

    // const handleChangePrice = (event) => {
    //     const { name, value } = event.target;
    //     console.log(name);

    //     setValues({
    //         ...values,
    //         [name]: value,
    //     });

    //     if (Number(value) === 10) {
    //         const price = initPrice - initPrice * 0.1;
    //         setPrice(price);
    //     } else {
    //         setPrice(initPrice);
    //     }
    // };

    const handleSubmit = (event) => {
        event.preventDefault();
        const result = validate(values);

        if (Object.keys(result).length === 0 && result.constructor === Object) {
            console.log(values);
        } else {
            setErrors(result);
        }
    };

    return {
        handleChange,
        handleSubmit,
        values,
        errors,
    };
};

export default useForm;
