import React from "react";
import parse from "html-react-parser";
import styles from "./ProductDetail.module.css";

const ProductDetail = ({ product }) => {
  return (
    <form className={styles.main}>
      <div className="col-span-2">
        <h1 className="text-3xl font-extrabold tracking-tight text-gray-900 sm:text-4xl flex justify-between">
          {product.name} <sapn>${product.price}</sapn>
        </h1>
        <div className="mt-4 space-y-6">{parse(product.description)}</div>
      </div>
      <div>
        <div className="aspect-w-1 aspect-h-1 rounded-lg overflow-hidden">
          <img
            src={product.primary_image.url_standard}
            alt={product.primary_image.description}
            className="w-full h-full object-center object-cover"
          />
        </div>
      </div>
      <div>

      </div>
    </form>
  );
};

export default ProductDetail;
