import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch } from "react-redux";

import parse from "html-react-parser";
import styles from "./ProductDetail.module.css";

const ProductDetail = ({ products }) => {
    const { id } = useParams();
    const dispatch = useDispatch();
    const index = products.findIndex((x) => x.id == id);
    const product = products[index];

    const radio = product.modifiers[1];
    const input = product.modifiers[0];

    useEffect(() => {
        dispatch({
            type: "Header",
            payload: {
                menu: "Shop",
                name: "Vitamins & Supplements",
                title: product.name,
                description: product.meta_description,
                price: product.price,
            },
        });
    }, []);

    return (
        // <div className=" bg-slate-400 mt-5">
        //     <div>{product.name}</div>
        //     <div>{product.price}</div>
        //     <div>{parse(product.description)}</div>

        //     <img
        //         src={product.primary_image.url_standard}
        //         alt={product.primary_image.description}
        //         width="100"
        //         height="200"
        //     />
        //     <input
        //         type={input.type}
        //         id={input.id}
        //         name={input.name}
        //         placeholder={input.display_name}
        //         required={input.required}
        //         maxLength={input.text_max_length}
        //     />
        //     <input
        //         type={input.type}
        //         id={input.id}
        //         name={input.name}
        //         placeholder={input.display_name}
        //         required={input.required}
        //         maxLength={input.text_max_length}
        //     />
        //     <input
        //         type="radio"
        //         id="onetime"
        //         name={radio.name}
        //         value="onetime"
        //     />
        //     <label for="onetime">{radio.option_values[0].label}</label>
        //     <input
        //         type="radio"
        //         id="monthly"
        //         name={radio.name}
        //         value="monthly"
        //     />
        //     <label for="monthly">{radio.option_values[1].label}</label>
        //     <button onClick={addToCartHandler}>Add to Cart</button>
        //     <br></br>
        // </div>
        <section>
            <div className={styles.productDetail}>
                <div className="image pt-5">
                    <img
                        src={product.primary_image.url_standard}
                        alt={product.primary_image.url_standard}
                    />
                </div>
                <div className="description pt-5">
                    {parse(product.description)}
                </div>
                <div className="form pt-5">
                    <form>
                        <div>
                            <label for="f-option" className="radio">
                                <input
                                    type="radio"
                                    id="f-option"
                                    name="selector"
                                    tabindex="1"
                                />
                                <span>First</span>
                            </label>
                            <label for="s-option" className="radio">
                                <input
                                    type="radio"
                                    id="s-option"
                                    name="selector"
                                    tabindex="2"
                                />
                                <span>Second</span>
                            </label>
                            <label for="t-option" className="radio">
                                <input
                                    type="radio"
                                    id="t-option"
                                    name="selector"
                                    tabindex="3"
                                />
                                <span>Third</span>
                            </label>
                        </div>
                    </form>
                </div>
            </div>
        </section>
    );
};

export default ProductDetail;
