import React from "react";

// const product = {
//   name: "Everyday Ruck Snack",
//   href: "#",
//   price: "$220",
//   description:
//     "Don't compromise on snack-carrying capacity with this lightweight and spacious bag. The drawstring top keeps all your favorite chips, crisps, fries, biscuits, crackers, and cookies secure.",
//   imageSrc:
//     "https://tailwindui.com/img/ecommerce-images/product-page-04-featured-product-shot.jpg",
//   imageAlt:
//     "Light green canvas bag with black straps, handle, front zipper pouch, and drawstring top.",
//   breadcrumbs: [
//     { id: 1, name: "Travel", href: "#" },
//     { id: 2, name: "Bags", href: "#" },
//   ],
//   sizes: [
//     { name: "18L", description: "Perfect for a reasonable amount of snacks." },
//     { name: "20L", description: "Enough room for a serious amount of snacks." },
//   ],
// };

const ProductItem = ({ product }) => {
  return (
    <div className="bg-gray-50">
      {/* {console.log(product)} */}
      <main>
        {/* Product */}

        <div className="max-w-2xl mx-auto pt-16 pb-24 px-4 sm:pt-24 sm:pb-32 sm:px-6 lg:max-w-7xl lg:px-8 lg:grid lg:grid-cols-2 lg:gap-x-8 bg-white">
          {/* Product details */}
          <div className="lg:max-w-lg lg:self-end">
            <div className="mt-4">
              <h1 className="text-3xl font-extrabold tracking-tight text-gray-900 sm:text-4xl">
                {product.name}
              </h1>
            </div>

            <section aria-labelledby="information-heading" className="mt-4">
              <h2 id="information-heading" className="sr-only">
                Product information
              </h2>

              <div className="flex items-center">
                <p className="text-lg text-gray-900 sm:text-xl">
                  {product.price}
                </p>
              </div>

              <div className="mt-4 space-y-6">
                {/* <p className="text-base text-gray-500">{product.description}</p> */}
                {/* <div dangerouslySetInnerHTML={{ __html: product.description.replace(/\n/g, "") }} /> */}
              </div>

              <div className="mt-6 flex items-center">
                {/* <CheckIcon className="flex-shrink-0 w-5 h-5 text-green-500" aria-hidden="true" /> */}
                <p className="ml-2 text-sm text-gray-500">
                  In stock and ready to ship
                </p>
              </div>
            </section>
          </div>

          {/* Product image */}
          <div className="mt-10 lg:mt-0 lg:col-start-2 lg:row-span-2 lg:self-center">
            <div className="aspect-w-1 aspect-h-1 rounded-lg overflow-hidden">
              <img
                src={product.primary_image.url_standard}
                alt={product.primary_image.description}
                className="w-full h-full object-center object-cover"
              />
            </div>
          </div>

          {/* Product form */}
          <div className="mt-10 lg:max-w-lg lg:col-start-1 lg:row-start-2 lg:self-start">
            <section aria-labelledby="options-heading">
              <h2 id="options-heading" className="sr-only">
                Product options
              </h2>

              <form>
                <div className="mt-4">
                  <a
                    href="#"
                    className="group inline-flex text-sm text-gray-500 hover:text-gray-700"
                  >
                    <span>What size should I buy?</span>
                  </a>
                </div>
                <div className="mt-10">
                  <button
                    type="submit"
                    className="w-full bg-indigo-600 border border-transparent rounded-md py-3 px-8 flex items-center justify-center text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-50 focus:ring-indigo-500"
                  >
                    Add to bag
                  </button>
                </div>
                <div className="mt-6 text-center">
                  <a
                    href="#"
                    className="group inline-flex text-base font-medium"
                  >
                    <span className="text-gray-500 hover:text-gray-700">
                      Lifetime Guarantee
                    </span>
                  </a>
                </div>
              </form>
            </section>
          </div>
        </div>
      </main>
    </div>
  );
};

export default ProductItem;
