import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch } from "react-redux";

import parse from "html-react-parser";
import styles from "./ProductDetail.module.css";

const ProductDetail = ({ products }) => {
    const { id } = useParams();
    const dispatch = useDispatch();
    const index = products.findIndex((x) => x.id == id);
    const product = products[index];

    const radio = product.modifiers[1];
    const input = product.modifiers[0];

    useEffect(() => {
        dispatch({
            type: "Header",
            payload: {
                menu: "Shop",
                name: "Vitamins & Supplements",
                title: product.name,
                description: product.meta_description,
                price: product.price,
            },
        });
    }, []);

    return (
        // <div className=" bg-slate-400 mt-5">
        //     <div>{product.name}</div>
        //     <div>{product.price}</div>
        //     <div>{parse(product.description)}</div>

        //     <img
        //         src={product.primary_image.url_standard}
        //         alt={product.primary_image.description}
        //         width="100"
        //         height="200"
        //     />
        //     <input
        //         type={input.type}
        //         id={input.id}
        //         name={input.name}
        //         placeholder={input.display_name}
        //         required={input.required}
        //         maxLength={input.text_max_length}
        //     />
        //     <input
        //         type={input.type}
        //         id={input.id}
        //         name={input.name}
        //         placeholder={input.display_name}
        //         required={input.required}
        //         maxLength={input.text_max_length}
        //     />
        //     <input
        //         type="radio"
        //         id="onetime"
        //         name={radio.name}
        //         value="onetime"
        //     />
        //     <label for="onetime">{radio.option_values[0].label}</label>
        //     <input
        //         type="radio"
        //         id="monthly"
        //         name={radio.name}
        //         value="monthly"
        //     />
        //     <label for="monthly">{radio.option_values[1].label}</label>
        //     <button onClick={addToCartHandler}>Add to Cart</button>
        //     <br></br>
        // </div>
        <section>
            <div className={styles.productDetail}>
                <div className="image pt-5">
                    <img
                        src={product.primary_image.url_standard}
                        alt={product.primary_image.url_standard}
                    />
                </div>
                <div className="description pt-5">
                    {parse(product.description)}
                </div>
                <div className="form pt-5 w-full col-span-2">
                    <form className="grid grid-cols-2 gap-x-5">
                        <div>
                            <div class="">
                                <div class="mt-6 flex w-full px-3 py-2 border border-gray-300 rounded-md hover:ring-indigo-500 focus:border-indigo-500 sm:text-sm">
                                    <input
                                        id="country-option-1"
                                        type="radio"
                                        name="countries"
                                        value="USA"
                                        class="w-4 h-4 border-gray-300 focus:ring-2 focus:ring-blue-300 "
                                        checked
                                    />
                                    <label
                                        for="country-option-1"
                                        class="block ml-2 text-sm font-medium text-gray-900"
                                    >
                                        United States
                                    </label>
                                </div>

                                <div class="flex items-center mb-4">
                                    <input
                                        id="country-option-2"
                                        type="radio"
                                        name="countries"
                                        value="Germany"
                                        class="w-4 h-4 border-gray-300 focus:ring-2 focus:ring-blue-300"
                                        aria-labelledby="country-option-2"
                                        aria-describedby="country-option-2"
                                    />
                                    <label
                                        for="country-option-2"
                                        class="block ml-2 text-sm font-medium text-gray-900 dark:text-gray-300"
                                    >
                                        Germany
                                    </label>
                                </div>
                            </div>
                        </div>
                        <div>
                            <div>
                                <label
                                    htmlFor="email"
                                    className="block text-sm font-medium text-gray-700"
                                >
                                    Email address
                                </label>
                                <div className="mt-1">
                                    <input
                                        id="email"
                                        name="email"
                                        type="text"
                                        autoComplete="email"
                                        required
                                        className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                                    />
                                </div>
                            </div>
                            <div className="mt-5">
                                <label
                                    htmlFor="email"
                                    className="block text-sm font-medium text-gray-700"
                                >
                                    Email address
                                </label>
                                <div className="mt-1">
                                    <input
                                        id="email"
                                        name="email"
                                        type="text"
                                        autoComplete="email"
                                        required
                                        className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                                    />
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </section>
    );
};

export default ProductDetail;
