import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";

import parse from "html-react-parser";
import styles from "./ProductDetail.module.css";
import useForm from "../../hook/useForm";
import validate from "../../validation/Validation"

const ProductDetail = ({ products }) => {
    const { handleChange, handleSubmit, values,errors } = useForm(validate);
    const { id } = useParams();
    const [price, setPrice] = useState(0);
    const dispatch = useDispatch();
    const index = products.findIndex((x) => x.id == id);
    const product = products[index];

    const radio = product.modifiers[1];
    const input = product.modifiers[0];

    const changePriceHandler = (event) => {
        if (event.target.value == 10) {
            let value = product.price;
            value = value - value * 0.1;
            setPrice(value);
        } else {
            setPrice(product.price);
        }
    };

    useEffect(() => {
        dispatch({
            type: "Header",
            payload: {
                menu: "Shop",
                name: "Vitamins & Supplements",
                title: product.name,
                description: product.meta_description,
            },
        });
        setPrice(product.price);
    }, []);

    return (
        <section>
            <div className={styles.productDetail}>
                <div className="image pt-5">
                    <img
                        src={product.primary_image.url_standard}
                        alt={product.primary_image.url_standard}
                    />
                </div>
                <div className="description pt-5">
                    <p className="text-base lg:text-2xl text-right mb-3">
                        USD ${price}
                    </p>
                    {parse(product.description)}
                </div>
                <div className="form pt-5 w-full col-span-2">
                    <form onSubmit={handleSubmit}>
                        <div className={styles.radio}>
                            <p className="block text-sm font-medium text-gray-700 mb-1 col-span-2">
                                How would you like to purchase this product?
                            </p>

                            <label htmlFor="onetime">
                                <input
                                    type="radio"
                                    id="onetime"
                                    name="purchace"
                                    value="0"
                                    className="mr-1"
                                    onChange={changePriceHandler}
                                    checked={price === product.price}
                                />
                                One-Time
                            </label>

                            <label htmlFor="subscribe">
                                <input
                                    type="radio"
                                    id="subscribe"
                                    name="purchace"
                                    value="10"
                                    onChange={changePriceHandler}
                                    className="mr-1"
                                    checked={price !== product.price}
                                />
                                Subscribe every 3 months and save 10%
                            </label>
                        </div>
                        <div className="mt-5">
                            <div>
                                <label
                                    htmlFor="customername"
                                    className="block text-sm font-medium text-gray-700"
                                >
                                    Who it's for:
                                </label>
                                <div className="mt-1">
                                    <input
                                        id="customername"
                                        name="customername"
                                        type="text"
                                        className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                                        value={values.customername}
                                        onChange={handleChange}
                                    />
                                </div>
                                {errors.customername && <p>{errors.customername}</p>}
                            </div>
                            <div className="mt-5">
                                <label
                                    htmlFor="allergies"
                                    className="block text-sm font-medium text-gray-700"
                                >
                                    List any allergies :
                                </label>
                                <div className="mt-1">
                                    <input
                                        id="allergies"
                                        name="allergies"
                                        type="text"
                                        className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                                    />
                                </div>
                            </div>
                        </div>
                        <button
                            type="submit"
                            className="mt-5 inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                        >
                            Add to Cart
                        </button>
                    </form>
                </div>
            </div>
        </section>
    );
};

export default ProductDetail;
