import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import parse from "html-react-parser";

const ProductItem = (props) => {
    const { id } = useParams();
    const products = props.products;
    const index = products.findIndex((x) => x.id == id);
    const product = props.products[index];

    const radio = product.modifiers[1];
    const input = product.modifiers[0];

    const addToCartHandler = () => {
        // and then send Http request
        // fetch('firebase-url', { method: 'POST', body: JSON.stringify(newCart) })
        // dispatch(
        //     cartActions.addItemToCart({
        //         id,
        //         title,
        //         price,
        //     })
        // );
    };

    return (
        <div className=" bg-slate-400 mt-5">
            <div>{product.name}</div>
            <div>{product.price}</div>
            <div>{parse(product.description)}</div>

            <img
                src={product.primary_image.url_standard}
                alt={product.primary_image.description}
                width="100"
                height="200"
            />
            <input
                type={input.type}
                id={input.id}
                name={input.name}
                placeholder={input.display_name}
                required={input.required}
                maxLength={input.text_max_length}
            />
            <input
                type={input.type}
                id={input.id}
                name={input.name}
                placeholder={input.display_name}
                required={input.required}
                maxLength={input.text_max_length}
            />
            <input
                type="radio"
                id="onetime"
                name={radio.name}
                value="onetime"
            />
            <label for="onetime">{radio.option_values[0].label}</label>
            <input
                type="radio"
                id="monthly"
                name={radio.name}
                value="monthly"
            />
            <label for="monthly">{radio.option_values[1].label}</label>
            <button onClick={addToCartHandler}>Add to Cart</button>
            <br></br>
        </div>
    );
};

export default ProductItem;
