import { createStore, combineReducers } from "redux";

const headerState = {
    menu: "Shop",
    name: "Shop",
    description: "Vitamins & Supplements",
    price: null,
};

const cartState = [{}];

const updateHeader = (state = headerState, action) => {
    console.log(state);

    if (action.type === "Header") {
        return action.payload;
    }
    return state;
};

const addCartData = (state = cartState, action) => {
    console.log(state);
    if (state.type === "Add") {
        console.log(state);
    }
    return state;
};

const store = createStore(combineReducers({ updateHeader, addCartData }));

export default store;
