import { useState } from "react";

const useForm = (validate, initPrice) => {
    const [values, setValues] = useState({
        customername: "",
        allergies: "",
        purchace: "",
    });
    const [errors, setErrors] = useState({});
    const [price, setPrice] = useState(initPrice);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setValues({
            ...values,
            [name]: value,
        });

        console.log(name);
        if (name !== "purchace") return;
        if (Number(value) === 10) {
            const price = initPrice - initPrice * 0.1;
            setPrice(price);
        } else {
            setPrice(initPrice);
        }
    };

    // const handleChangePrice = (event) => {
    //     const { name, value } = event.target;
    //     console.log(name);

    //     setValues({
    //         ...values,
    //         [name]: value,
    //     });

    //     if (Number(value) === 10) {
    //         const price = initPrice - initPrice * 0.1;
    //         setPrice(price);
    //     } else {
    //         setPrice(initPrice);
    //     }
    // };

    const handleSubmit = (event) => {
        event.preventDefault();
        const result = validate(values);

        if (Object.keys(result).length === 0 && result.constructor === Object) {
            console.log(values);
        } else {
            setErrors(result);
        }
    };

    return {
        handleChangePrice,
        handleChange,
        handleSubmit,
        values,
        errors,
        price,
    };
};

export default useForm;
